import Dropdown from 'react-bootstrap/Dropdown';
import { Container } from 'react-bootstrap'

function ErrorList (props) {
    const [visible, setVisible] = useState(false)
    const str ="<div style ={{color: 'red'}}> red </div>"
    // style={{display: 'flex'}}

    function concat (arr) {
        var txt = '';
        arr.forEach ((t) => {txt += t + '  '})
        return txt;
    }

    function clear() {
        console.log ('clear', props.errorList)
        props.setErrors([])
        // beep2()
    }


    return (  
  
        <div  >   
            <div style={{display: 'flex'}}>
                <div style={{color: 'red'}} ><input type="checkbox" checked={visible} onChange={() => {setVisible (!visible)}} /> errors ({props.errorList.length}) </div>
                 &nbsp; &nbsp; <button type="button" onClick={()=>clear()}>Clear </button> 
            </div>

            <Container  className='d-flex_ align-items-left justify-content-left_' style={{maxHeight: "25vh", padding: '0px', margin:'0px'}}  > 
            {/* <hr/>  */}
            <div className="container_" style ={{overflow: 'scroll', hight: '17%', width: '580px'}} >
                { visible && props.errorList.map((err) => (
                    // display each err in array in one line
                <div style={{display: 'flex'}} key={concat(err)} >
                    <div style={{display: 'flex', margin: '0px'}} > {concat (err)} </div> 
                </div>
                ))
                }
            </div>
           </Container>
        </div>

    )

}



function beep(vol, freq, duration){
var a=new AudioContext() // browsers limit the number of concurrent audio contexts, so you better re-use'em
var v=a.createOscillator()
  var u=a.createGain()
  v.connect(u)
  v.frequency.value=freq
  v.type="square"
  u.connect(a.destination)
  u.gain.value=vol*0.01
  v.start(a.currentTime)
  v.stop(a.currentTime+duration*0.001)
}

function beep2()
{ var snd = new Audio("data:audio/wav;base64,");  
  snd.play();
}

export {ErrorList, beep, beep2}